import { Component, OnInit } from '@angular/core';
import { BaseService } from '../../services/base.service';
import { Observable, Subscribable, Subscription } from 'rxjs';
import {CompanyInformationService} from "../../services/company-information.service";
import {NgxSpinnerService} from "ngx-spinner";
import {Router} from "@angular/router";
import {MainService} from "../../services/main.service";
import { environment } from 'src/environments/environment';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {
  checkHeadNav: any;
  checkToggleBar: any;
  toggleValue: any;
  subscription: any;
  pic:any;
  usersession:any;
  data:any;
  empname:any;
  email:any;
  imageurls:any;
  companyName: any;
  companyinfo: any;
  activeModule:any;
  companyDBName: any = environment.dbName;
  infodata: any;
  valid: boolean = false;
  flag: boolean = true;
  selectedModule:any='';
  constructor(private baseService: BaseService,private mainService:MainService,
    private LM: CompanyInformationService, private spinner: NgxSpinnerService, public router: Router) {
      this.usersession = JSON.parse(sessionStorage.getItem('user') ?? '');
      if (this.usersession.firstlogin == "N") {
        this.flag = true;
      }
      else {
        this.flag = false;
      }
     }

  ngOnInit(): void {
    this.companyName='Sreeb Technologies';
    this.baseService.setHeadNav("admin");
    this.getHeadNav();
    this.getToggleSideBar()
    this.usersession =JSON.parse(sessionStorage.getItem('user')??'');
    this.selectedModule =sessionStorage.getItem('selectedModule')??'';
   // this.activeModule = JSON.parse(sessionStorage.getItem('activeModule') || '');
    // this.getLogo()
    // this.getUploadImage();
    this.getCompanyInformation();
    this.empname = this.usersession.firstname;
    this.email = this.usersession.officeemail;
  }
  logout(){
    sessionStorage.removeItem('user')
    sessionStorage.removeItem('sidemenu');
    sessionStorage.removeItem('activeModule');
    sessionStorage.removeItem('token');
    var login = '/Login'
    this.router.navigate([login]);
    sessionStorage.removeItem('companyName');
    sessionStorage.clear();
  }
  onClickMainDashboard(){
    this.router.navigate(['/main/MainDashboard']);
    sessionStorage.setItem('selectedModule','Spryple' );
    this.selectedModule =sessionStorage.getItem('selectedModule')??'';
  }
  setHeadNav(data:any): void {
    this.baseService.setHeadNav(data);
    this.baseService.clearSubSideNav();
    this.baseService.clearToggleSideBar();
    if(data === "self")
      this.baseService.setSideNav("dashboard");
    else if(data === "myTeam")
      this.baseService.setSideNav("leaveManagement");
    else if(data === "admin")
      this.baseService.setSideNav("adminDashboard");
      this.setToggleSideBar("Show");

  }
  clearHeadNav(): void {
    // clear messages
    this.baseService.clearHeadNav();
  }
  getHeadNav(): void
  {
    this.baseService.getHeadNav().subscribe((message:any) => {
      if (message) {
        this.checkHeadNav = message;
      } else {
        this.checkHeadNav = '';
      }
    });
  }
  setToggleSideBar(data:any): void
  {
    this.baseService.setToggleSideBar(data);
    if(data == "Show") {
      this.toggleValue = "Hide";
    }
    else if(data == "Hide")
    {
      this.toggleValue = "Show";
    }
  }
  getToggleSideBar(): void
  {
    this.baseService.getToggleSideBar().subscribe((message:any) => {
      if (message) {
        this.checkToggleBar = message;
      } else {
        this.checkToggleBar = '';
      }
    });
  }
  changePassword(){
    sessionStorage.setItem('selectedModule','Change Password');
    this.router.navigate(['Attendance/ChangePassword'])
  }

  editProfile(){
    sessionStorage.setItem('selectedModule','Edit Profile');
    this.router.navigate(['LeaveManagement/EditProfile']);
  }
  getLogo()
  {
    let info ={
      employeeId: 0,
      candidateId: null,
      filecategory: 'LOGO',
      moduleId: 2,
      requestId: null,
      status: 'Submitted',
    };
    this.mainService.getFilesMaster(info).subscribe((result) => {
      if(result && result.status &&  result.data[0]){
       result.data[0].employeeId=0;
       let info = result.data[0]
       this.mainService.getProfileImage(result.data[0]).subscribe((imageData) => {
        if(imageData.success){
          let TYPED_ARRAY = new Uint8Array(imageData.image.data);
          const STRING_CHAR = TYPED_ARRAY.reduce((data, byte)=> {
            return data + String.fromCharCode(byte);
          }, '');

          let base64String= btoa(STRING_CHAR)
          this.pic='data:image/png;base64,'+base64String;


        }
      else{
        // this.isRemoveImage=false;
        this.pic ="data:image/png;base64,";

      }
    })
  }else{
    // this.isRemoveImage=false;
    this.pic ="data:image/png;base64,";

  }
})

  }

  getUploadImage(){
    let info = {
      employeeId: this.usersession.id,
      candidateId: null,
      filecategory: 'PROFILE',
      moduleId: 1,
      requestId: null,
      status: 'Submitted',
    };
    this.mainService.getFilesMaster(info).subscribe((result) => {
      // if(result && result.status &&  result.data[0]){
      //  result.data[0].employeeId=this.usersession.id;
      //   let info = result.data[0]
      this.valid = false;
      if (result && result.status && result.data.length > 0) {

        for (let i = 0; i < result.data.length; i++) {
          if (result.data[i].file_category == 'PROFILE') {
            result.data[i].employeeId = this.usersession.id;
            this.infodata = result.data[i]
            this.valid = true;
            break;
          }
          else {
            this.imageurls = '';
          }
        }
        if (this.valid) {
          this.mainService.getProfileImage(result.data[0]).subscribe((imageData) => {
            if (imageData.success) {
              let TYPED_ARRAY = new Uint8Array(imageData.image.data);
              const STRING_CHAR = TYPED_ARRAY.reduce((data, byte) => {
                return data + String.fromCharCode(byte);
              }, '');

              let base64String = btoa(STRING_CHAR)
              this.imageurls = 'data:image/png;base64,' + base64String;
            }
            else {
              this.imageurls = '';

            }
          })
        } else {
          this.imageurls = '';

        }

      }
      else {
        this.imageurls = '';
      }
    })
  }
  getCompanyInformation(){
    this.LM.getCompanyInformation('companyinformation',null,1,10,this.companyDBName).subscribe((data:any)=>{
      if(data.status && data.data.length!=0) {

        this.companyinfo =data.data[0];
        this.companyName=data.data[0].companyname;

      }else {
        // this.enable=true;
        // this.isSubmit=true;
        // this.companyForm=[];

      }

    })

  }

}



